import {useParams,useNavigate,useLocation} from "react-router-dom";
import React, {useState, useRef} from 'react';
import '../css/detail.css';



function Detail({hd,items}){
    let locate = useLocation();
    hd(locate.pathname);
    const navigate = useNavigate();
    
    const goBack = () =>{
        navigate(-1);
    }

    let { id } = useParams();
    let matchItems = items.work;
    let movie_key = matchItems[id - 1].num;

    if(movie_key == id ){
    return(
        
    <div className="detail_1">
      
         <div className="title_1">
            <p className="t_img"><img src={matchItems[movie_key-1].img[1]} /></p> 
            <h2 className="title">{matchItems[movie_key-1].title}</h2>
          
         </div>
         
       <div className="pro_detail">
            <p><span className="de_name">프로젝트명 :</span> {matchItems[movie_key-1].title}</p>
            <p><span className="de_name">소개 :</span>{matchItems[movie_key-1].detail}</p>
            <p><span className="de_name">개발 기간 : </span> {matchItems[movie_key-1].date}</p>
            <p><span className="de_name">개발 언어 :</span>{matchItems[movie_key-1].tool}</p>
            <p><span className="de_name">기여도 :</span>{matchItems[movie_key-1].contribution}</p>
            <p><span className="de_name">링크 :</span> <a href={matchItems[movie_key-1].url}>{matchItems[movie_key-1].url}</a></p>
       </div>
       <div className = "img">
           <p className="iframe_wrap">VIEW</p>
           <iframe src={matchItems[movie_key-1].url} className={matchItems[movie_key-1].style} ></iframe>
         
       </div>
      

        <button className="back" onClick={goBack}>X HOME</button>
    </div>
   

     );
    }
   
}
export default Detail;
