import {useParams,useNavigate,useLocation} from "react-router-dom";
import React, {useState, useRef,useEffect} from 'react';
import '../css/detail.css';



function Detail({hd,items}){
    let locate = useLocation();
    hd(locate.pathname);
    const navigate = useNavigate();
    
    const goBack = () =>{
        navigate(-1);
    }
    useEffect(() => {
        window.scrollTo(0, 0);
      }, [locate]);
    
  
    let { id } = useParams();
    let matchItems = items.work;
    let movie_key = matchItems[id - 1].num;
  
    if(movie_key == id ){
    return(
        
    <div className="detail_1">
      
         <div className="title_1">
            <p className="t_img"><img src={matchItems[movie_key-1].img[1]} /></p> 
            <h2 className="title">{matchItems[movie_key-1].title}</h2>
          
         </div>
         
       <ul className="pro_detail">
            <li><span className="de_name">NAME </span>{matchItems[movie_key-1].title}</li>
            <li><span className="de_name">ABOUT </span>{matchItems[movie_key-1].detail}</li>
            <li><span className="de_name">DEVELOPMENT PERIOD  </span> {matchItems[movie_key-1].date}</li>
            <li><span className="de_name">TOOL </span>{matchItems[movie_key-1].tool}</li>
            <li><span className="de_name">CONTRIBUTION </span>{matchItems[movie_key-1].contribution}</li>
            <li><span className="de_name">SITE </span><a className="link" href={matchItems[movie_key-1].url}>{matchItems[movie_key-1].url}</a></li>
       </ul>
       <div className = "img">
           <p className="iframe_view">VIEW</p><br />
           <iframe src={matchItems[movie_key-1].url} className={matchItems[movie_key-1].style} ></iframe>
         
       </div>
      

        <button className="back" onClick={goBack}> X HOME</button>
    </div>
   

     );
    }
   
}
export default Detail;
