import d from "../skill.json";
import React,{useState,useEffect,useRef} from "react"
import {HashRouter,Route,Routes,Link} from "react-router-dom";
import '../css/work.css';



function Work(){
    let w_data = [];
    let item = useRef();
    const[wlist,setWlist] = useState([]);
const [isShown,setIsShown] = useState(false);

const change = (e) =>{
    console.log(e)
}
// const hoverHandler = () =>{
//     console.log("onMouseEnter");
//     setIsShown(true);
// }
// const outHandler = () =>{
//     console.log("onMouseLeave");
//     setIsShown(false);
// }
useEffect(() => {  
    const list = d.work;
    list.forEach((value) =>{
        w_data.push(value);
    })
    setWlist(w_data);

},[]);

    return(
        <>
            <div className="third">
                <div className="third_1">
                        <span className="num">02</span>

                </div>
        </div>

        <div className="third2">
            <h1 className="title">
                <span className="text">Work</span>
                <div className="title_l">
                    <span className="s_name1">WORKS</span><br />
                </div>
            </h1>

        </div>


        <div className="work_l">
           <ul className="w_ul"  >
           {
                         wlist?.map(function(v,k){
                        return(
                            <li className="w_li"    key = {k} 
                                    onClick={change}
                                    ref={item}
                                     >
                            {/* <Link to={`/Detail/${v.num}`} > */}
                                    {v.title}
                                    {/* </Link>            */}
                                </li> 
                            )
                        })
                    }

           </ul>

            <p className="imgs_1"><img src="../img/work.png"/></p>
            <p className="imgs_2"><img src="../img/work2.png"/></p>
            <p className="imgs_3"><img src="../img/work.png"/></p>

        </div>
    </>
    );
}
export default Work;
