import {useParams,useNavigate} from "react-router-dom";
import {useSelector, useDispatch} from "react-redux";
import React, {useState, useRef} from 'react';
import '../css/detail.css';
import styled, { css } from "styled-components";


function Detail(props ){
   
    const navigate = useNavigate();

    const goBack = () =>{
        navigate(-1);
    }
    let { id } = useParams();
    let matchItems = props.items.work;
    let movie_key = matchItems[id - 1].num;

    if(movie_key == id ){
    return(
        
    <div className="detail_1">
         <button className="back" onClick={goBack}>X HOME</button>
         <div className="title_1">
            <h2 className="title">{matchItems[movie_key-1].title}</h2>
           <p className="t_img"><img src={matchItems[movie_key-1].img[0]} /></p> 
         </div>
       
        <p className="title1">{matchItems[movie_key-1].contribution}</p>

   
    </div>
   

     );
    }
}
export default Detail;
