import d from "../skill.json";
import React,{useState,useEffect,useRef} from "react"
import {HashRouter,Route,Routes,Link} from "react-router-dom";
import '../css/work.css';



function Work(){
    let w_data = [];
    let w_data2 = [];

    let number = '';
    let item = useRef();
    const[wlist,setWlist] = useState([]);
    const[wlist2,setWlist2] = useState([]);
    const[wnum,setWnum] = useState();

const [isShown,setIsShown] = useState(false);



const hoverHandler = (e) =>{

    setIsShown(true);
    setWnum(e.target.dataset.num);
    console.log(wnum)


}
// const outHandler = () =>{
//     console.log("onMouseLeave");
//     setIsShown(false);
// }
useEffect(() => {  
    const list = d.work;
    list.forEach((value) =>{
        w_data.push(value);
        w_data2.push(value.img);
      //  w_data2.push(value);
    })
    setWlist(w_data);
    setWlist2(w_data2);

},[]);



    return(
        <>
            <div className="third">
                <div className="third_1">
                        <span className="num">02</span>

                </div>
        </div>

        <div className="third2">
            <h1 className="title">
                <span className="text">Work</span>
                <div className="title_l">
                    <span className="s_name1">WORKS</span><br />
                </div>
            </h1>

        </div>


        <div className="work_l">
           <ul className="w_ul"  >
           {
                         wlist?.map(function(v,k){
                        return(
                            <li className="w_li"  key = {k} 
                             onMouseEnter={hoverHandler}
                                    ref={item}
                                    data-num={k}
                                     >
                            {/* <Link to={`/Detail/${v.num}`} > */}
                                    {v.title}
                                    {/* </Link>            */}
                                </li> 
                            )
                        })
                    }

           </ul>

           <p className="imgs_1" > <img src={w_data2[{wnum}]}  /></p>
           <p className="imgs_2" > <img src={[{wnum}]}  /></p>
           <p className="imgs_3"> <img src={[{wnum}]}  /></p>
        </div>
    </>
    );
}
export default Work;
