useEffect(() => {  
    const list = d.work;
    list.forEach((value) =>{
        w_data.push(value);
        w_data2.push(value.img);
      //  w_data2.push(value);
    })
    setWlist(w_data);

},[]);
console.log(wlist)


    return(
        <>
            <div className="third">
                <div className="third_1">
                        <span className="num">02</span>

                </div>
        </div>

        <div className="third2">
            <h1 className="title">
                <span className="text">Work</span>
                <div className="title_l">
                    <span className="s_name1">WORKS</span><br />
                </div>
            </h1>

        </div>


        <div className="work_l">
           <ul className="w_ul"  >
           {
                         wlist?.map(function(v,k){
                        return(
                            <li className="w_li"    key = {k}  onMouseEnter={hoverHandler}

                                    ref={item}
                                    data-num={k}
                                     >
                            {/* <Link to={`/Detail/${v.num}`} > */}
                                    {v.title}
                                    {/* </Link>            */}
                                </li> 
                            )
                        })
                    }

           </ul>
            {/* <p className="imgs_1"><img src="../img/work.png" /></p>
            <p className="imgs_2"><img src="../img/work2.png"/></p> 
            <p className="imgs_3"><img src="../img/work.png"/></p>
         */}

        </div>
    </>
    );
}
export default Work;
