import './App.css';
import './css/common.css';
import {HashRouter,Route,Routes,Link} from "react-router-dom";
import React,{useState,useEffect,useRef} from "react";
import Main from './component/Main';
import Detail from './component/Detail';
import d from "./skill.json";
import AOS from "aos";
function App() {
 const  nav = useRef();
 const [ScrollY,setScrollY] = useState(0);
 // const [BtnStatus,setBtnStatus] = useState(null);
 let [items,setItems] =useState(d);
 let [blue,setBlue] = useState(null);



 const change = (c) =>{
      if( c ==='/'){
        setBlue('');
      }else{
        setBlue('change');
      }
    }

  const show =(e) =>{
    for(let i = 0; i < 3; i++){
    nav.current.children[i].children[0].classList.remove('active');
    }
    e.target.classList.add('active');
  }

 const handleFollow = () =>{
   setScrollY(window.pageYOffset);

    if(ScrollY > 100){
     setBtnStatus(true);
    }else{
      setBtnStatus(false);

    }
   }


   const handleTop = () =>{
     window.scrollTo({
       top:0,
       behavior:"smooth"
     });
     setScrollY(0);
    // setBtnStatus(false);
     for(let i = 0; i < 3; i++){
        nav.current.children[i].children[0].classList.remove('active');
      }
        nav.current.children[0].children[0].classList.add('active');
   }

   const handleTop2 = () =>{
    window.scrollTo({
      top:3550,
      behavior:"smooth"
    });
    setScrollY(0);
   // setBtnStatus(false);
  }

  const handleTop3 = () =>{
    window.scrollTo({
      top:5470,
      behavior:"smooth"
    });
    setScrollY(0);
   // setBtnStatus(false);
  }


  useEffect(()=>{

    AOS.init();

     const watch = () =>{
       window.addEventListener('scroll',handleFollow);
     }

     watch();
     return () =>{
       window.removeEventListener('scroll',handleFollow);
     }
   },[ScrollY])

  return (
    <HashRouter >
      <div className={`total ${blue}`} >
         <div className= "grid-wrap">
                <div className={`grid ${blue}`}>
                    <div></div>
                    <div></div>
                    <div></div>
                </div>

            </div>
            <div  className={`logo ${blue}`} >
                <Link to ="/" onClick={handleTop}  >
                    <div className="barcode">
                      <img src="../img/icon.png" />
                      </div>
                      <div className="logo-info">
                        <div>
                          <span><i className="from-top">Y</i></span><span><i className="from-bottom">O</i></span><span><i className="from-left" >U</i></span>&nbsp;<span><i className="from-top" >J</i></span><span><i className="from-left" >U</i></span>&nbsp;<span><i className="from-top" >Y</i></span><span><i className="from-right" >E</i></span><span><i className="from-left">O</i></span><span><i className="from-bottom" >N</i></span>
                        </div>
                        <div>
                          <span><i className="from-top">P</i></span><span><i className="from-left" >O</i></span><span><i className="from-bottom" >R</i></span><span><i className="from-top" >T</i></span><span><i className="from-right">F</i></span><span><i className="from-bottom" >O</i></span><span><i className="from-left" >L</i></span><span><i className="from-left" >I</i></span><span><i className="from-left" >O</i></span>
                        </div>
                        <div>
                        <span><i className="from-top">@</i></span><span><i className="from-left" >2</i></span><span><i className="from-bottom" >0</i></span><span><i className="from-top" >2</i></span><span><i className="from-right">2</i></span>
                      </div>
                  </div>
                </Link>  
            </div>
          <div className={`menu ${blue}`}> 
            <ul className="nav" ref={nav} >
                <li className="anchor" data-num='0' onClick={show}><p  className="active" onClick={handleTop}>  ABOUT </p></li>
                <li className="anchor" data-num='1'onClick={show}><p onClick={handleTop2}> WORK </p></li>
                <li className="anchor" data-num='2' onClick={show}><p onClick={handleTop3}>CONTACT</p></li>
            </ul>
            <div className="square">
              <img className="sq"src="../img/Artboard.png" />
              <p className ="cir"><span ></span><span ></span><span ></span></p>
              </div>
          </div>


          <main>
            <Routes>
              <Route path="/" element={<Main hd={change} />}></Route>
              <Route path="/Detail/:id" element={<Detail hd={change} items={items}/>}></Route>


            </Routes>
          </main>
        </div>
    </HashRouter>
  );
}

export default App;
