import React,{useState,useEffect,useRef} from "react"
import {HashRouter,Route,Routes,Link} from "react-router-dom";
import '../css/work.css';



function Work(){

const [isShown,setIsShown] = useState(false);


const hoverHandler = () =>{
    console.log("onMouseEnter");
    setIsShown(true);
}
const outHandler = () =>{
    console.log("onMouseLeave");
    setIsShown(false);
}


    return(
        <>
            <div className="third">
                <div className="third_1">
                        <span className="num">02</span>

                </div>
        </div>

        <div className="third2">
            <h1 className="title">
                <span className="text">Work</span>
                <div className="title_l">
                    <span className="s_name1">WORKS</span><br />
                </div>
            </h1>

        </div>


        <div className="work_l">
           <ul className="w_ul"  >
               <li className="w_li" onMouseEnter={hoverHandler} onMouseOut ={outHandler} >
                   <Link to="/Detail">Discovery Land Company (웹 클론)</Link></li>

                   {isShown && (
                    <div className="h_img" style={{transform:"translateX(500px"}}>
                        <img src="../img/work.png" />
                    </div>
                      )}

                <li className="w_li"  onMouseEnter={hoverHandler} onMouseOut ={outHandler}>앤티앤스 프레즐 (모바일 클론)</li>
                {isShown && (
                    <div className="h_img" style={{transform:"translateX(500px"}}>
                        <img src="../img/work.png" />
                    </div>
                      )}
                <li className="w_li">스케쥴 사이트(php,plugin)</li>
                <li className="w_li">영화사이트 (모바일:React)</li>
           </ul>

            <p className="imgs_1"><img src="../img/work.png"/></p>
            <p className="imgs_2"><img src="../img/work2.png"/></p>
            {/* <p className="imgs_3"><img src="../img/work.png"/></p> */}

        </div>
    </>
    );
}
export default Work;
